import React, { useState, useEffect } from 'react'
import { fetchRepos } from 'dataLayer'
import Loading from 'Shared/Loading'

export default ({ user }) => {
  const [repos, setRepos] = useState([])
  const [loading, setLoading] = useState(false)

  useEffect(() => {
    setLoading(true)

    fetchRepos(user.login).then(repos => {
      setLoading(false)
      setRepos(repos)
    })
  }, [])

  if (loading) {
    return <Loading />
  }

  if (repos.length === 0) {
    return <div className="tc f7">No repos found</div>
  }

  return (
    <ul className="list pa2 overflow-y-auto h4">
      {repos.map(repo => (
        <li key={repo.name} className="f7 truncate">
          {repo.name}
        </li>
      ))}
    </ul>
  )
}
